import React from 'react'

const List = ({todoList,setTodo }) =>{


    //I'm going to create a function that fetch the todo list
    const removeItem = (item) => {
        setTodo(todoList.filter(i=> i.id !== item.id))
    }

    const displayList = () => {
        return(
            <div>
                {
                    todoList.map(item => {
                        return(
                            <div className="task_item">
                                <li>{item.todo} <button onClick={removeItem(item)}><i class="bi bi-trash-fill"></i></button></li>
                            </div>
                        )
                    })
                }
            </div>
        )
    }

    return(
        <div className="list_container">
            <ul>
                {displayList()}
            </ul>
        </div>
    )
}

export default List
